// src/BookDetail/BookDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './BookDetail.css'; // Ensure this path matches the location of your CSS file

const BookDetail = () => {
  const { id } = useParams(); // Extract the book ID from the URL
  const [book, setBook] = useState(null); // State to hold book details

  useEffect(() => {
    // Fetch book details from the backend using the book ID
    fetch(`http://localhost:8081/api/books/${id}`)
      .then(response => response.json())
      .then(data => setBook(data)) // Set the fetched data to the book state
      .catch(error => console.error("There was an error fetching the book details:", error));
  }, [id]); // Effect dependency on id ensures it runs when id changes

  // Display loading text until book data is fetched
  if (!book) return <div>Loading...</div>;

  // Render book details
  return (
    <div className="book-detail-container">
      <h2>{book.name}</h2>
      <p>Author: {book.author}</p>
      <p>ISBN: {book.isbn}</p>
      {/* You can add more details here as needed */}
    </div>
  );
};

export default BookDetail;
