// src/BookCatalog/BookCatalog.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link for navigation
import './BookCatalog.css'; // Adjust the path according to your file structure

const BookCatalog = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8081/api/books')
      .then(response => response.json())
      .then(data => setBooks(data))
      .catch(error => console.error("There was an error fetching the books:", error));
  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <div className="book-catalog">
      <h2>Book Catalog</h2>
      <ul>
        {books.map(book => (
          <li key={book.id}>
            <Link to={`/books/${book.id}`}>{book.name}</Link> - {book.author}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookCatalog;
