import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import './Register.css';

const Register = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // State for managing error messages
  const [success, setSuccess] = useState(''); // State for managing success messages

  const navigate = useNavigate(); // Initialize useNavigate for navigation

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(''); // Reset error messages on new submission
    setSuccess(''); // Reset success messages on new submission

    try {
      const response = await fetch('http://localhost:8081/api/user/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }), // Removed email from the payload
      });

      if (!response.ok) {
        // Attempt to read error message from the response, otherwise use statusText
        const errorText = await response.text();
        setError(`Registration failed: ${errorText || response.statusText}`);
        return; // Prevent further execution in case of error
      }

      const data = await response.json();
      console.log('Registration successful:', data);
      setSuccess('Registration successful. Please login.'); // Set success message

      // Redirect the user to the login page after successful registration
      navigate('/login');
    } catch (error) {
      console.error('An error occurred during registration:', error);
      setError('An unexpected error occurred. Please try again.'); // Set a generic error message
    }
  };

  return (
    <div className="register-container">
      <h1>Register</h1>
      {error && <div className="register-error">{error}</div>} {/* Display error message if any */}
      {success && <div className="register-success">{success}</div>} {/* Display success message if any */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
